export class Strip {
    constructor(config: any);
    ratio: number;
    groups: any[];
    width: number;
    height: number;
    isFullWidth: boolean;
    idx: any;
    groupsPerStrip: any;
    oneRow: any;
    targetItemSize: any;
    container: any;
    addGroup(group: any): void;
    markAsIncomplete(): void;
    canRemainIncomplete(): boolean;
    setWidth(width: any): void;
    resizeToHeight(height: any): void;
    isFull(newGroup: any, isLastImage: any): boolean;
    get hasGroups(): boolean;
    get lastGroup(): any;
    get scheme(): {
        idx: any;
        groups: any[];
        width: number;
        height: number;
        ratio: number;
        isFullWidth: boolean;
    };
}
